mapper.select:
select count(*)
from academy
where business_regis_num = #{businessRegisNum}

mapper.insert:
insert into academy
( academy_id
, created_at
, academy_name
, academy_address
, academy_detail_addr
, academy_postalcode
, academy_owner
, academy_contect
, business_regis_num
, registered_at)
values ( #{academyId}
       , current_timestamp
       , #{academyName}
       , #{academyAddress}
       , #{academyDetailAddr}
       , #{academyPostalcode}
       , #{academyOwner}
       , #{academyContect}
       , #{businessRegisNum}
       , #{registeredAt})
