mapper.insert:
INSERT INTO calendar
( calendar_id
, created_at
, google_calendar_id
, access_token
, refresh_token
, expires_in
, academy_id
, teacher_id)
VALUES ( #{calendarId}
       , current_timestamp
       , #{googleCalendarId}
       , #{accessToken}
       , #{refreshToken}
       , #{expiresIn}
       , #{academyId}
       , #{teacherId})

mapper.select:
SELECT refresh_token
FROM calendar
WHERE deleted_at IS NULL
  AND teacher_id = #{teacherId}
  AND academy_id = #{academyId}

mapper.update:
UPDATE calendar
SET access_token  = #{newAccessToken}
  , refresh_token = #{refreshToken}
  , expires_in    = #{newExpiresIn}
  , updated_at = current_timestamp
WHERE deleted_at IS NULL
  AND academy_id = #{academyId}
  AND teacher_id = #{teacherId}
