mapper.select:
SELECT *
FROM(  SELECT ROW_NUMBER() OVER (ORDER BY created_at DESC) AS rnum, qna_subject, qna_id, deleted_at
       FROM qna
       WHERE academy_id = #{academyId}
    )AS temp
WHERE  deleted_at IS NULL
  AND rnum > #{rnum}
ORDER BY rnum ASC
    LIMIT 1

SELECT *
FROM(  SELECT ROW_NUMBER() OVER (ORDER BY created_at DESC) AS rnum, qna_subject, qna_id, deleted_at
       FROM qna
       WHERE academy_id = #{academyId}
    )AS temp
WHERE deleted_at IS NULL
  AND rnum < #{rnum}
ORDER BY rnum DESC
    LIMIT 1

mapper.update:
UPDATE qna
SET qna_readcount = qna_readcount + 1
WHERE qna_id = #{id}

UPDATE qna
SET qna_answer_status = 1
WHERE qna_id = #{id}

mapper.select:
SELECT *
FROM qna
WHERE qna_id = #{id}
  AND qna_password = #{pass}

mapper.update:
UPDATE qna
SET deleted_at = NOW()
WHERE qna_id = #{updateId}

UPDATE qna
SET qna_answer_status = 0
WHERE qna_id = #{qnaId}
