mapper.select:
SELECT academy_id
FROM teacher
WHERE teacher_email=#{teacherEmail}

sql.if:
AND (
review_subject student_name student_email DATE_FORMAT(created_at, '%y-%m-%d') teacher_name
LIKE #{search_word}
)

mapper.select:
SELECT review_re_ref
FROM review
WHERE review_id = #{reviewId}

mapper.delete:
DELETE FROM review
WHERE academy_id=#{academyId}
AND review_re_ref=#{reviewReRef}

AND review_re_lev > 0

mapper.update:
UPDATE review
SET review_exposure_stat=#{reviewExposureStat}
WHERE academy_id=#{academyId}
AND review_id=#{reviewId}

UPDATE review
SET review_subject=#{reviewSubject}, review_content=#{reviewContent}, updated_at=CURRENT_TIMESTAMP()
WHERE academy_id=#{academyId}
AND review_id=#{reviewId}
